let canvas=document.getElementsByClassName("rain")[0];canvas.width=window.innerWidth,canvas.height=window.innerHeight;let c=canvas.getContext("2d");function randomNum(t,i){return Math.floor(Math.random()*t)+i}function RainDrops(t,i,n,a,e){this.x=t,this.y=i,this.endy=n,this.velocity=a,this.opacity=e,this.draw=function(){c.beginPath(),c.moveTo(this.x,this.y),c.lineTo(this.x,this.y-this.endy),c.lineWidth=1,c.strokeStyle="rgba(255, 255, 255, "+this.opacity+")",c.stroke()},this.update=function(){let t=window.innerHeight+100;this.y>=t?this.y=this.endy-100:this.y=this.y+this.velocity,this.draw()}}let rainArray=[];for(let t=0;t<140;t++){let t=Math.floor(Math.random()*window.innerWidth)+1,i=-500*Math.random(),n=randomNum(10,2),a=randomNum(20,.2),e=.55*Math.random();rainArray.push(new RainDrops(t,i,n,a,e))}function animateRain(){requestAnimationFrame(animateRain),c.clearRect(0,0,window.innerWidth,window.innerHeight);for(let t=0;t<rainArray.length;t++)rainArray[t].update()}